import { useCounter } from '../hooks/useCounter';

export const ContadorConHook = () => {

    const { valor, acumular } = useCounter

    return (
        <>
            <h3>Contador con Hook:
                <small>{valor}</small>
            </h3>
            <button
                className='btn btn-primary'
                onClick={() => acumular(+1)}>
                +1
            </button>
            {/* &nbsp es una pequeña separación */}
            &nbsp;
            <button
                className='btn btn-primary'
                onClick={() => acumular(-1)}>
                -1
            </button>
        </>
    )
}
