import React from 'react'
import { useEffect, useState, useRef } from 'react';
import { reqResApi } from '../api/reqRes';
import { ReqResListado, Usuario } from '../interfaces/reqRes';

export const Usuarios = () => {
    //Colección de todos los usuarios
    const [usuarios, setUsuarios] = useState<Usuario[]>([]);

    //Crear use state para la compaginación
    const paginaRef = useRef(1);

    useEffect(() => {
        cargarUsuarios();
    }, [])

    const cargarUsuarios = async () => {
        //llamado de API
        const resp = await reqResApi.get<ReqResListado>('/users', {
            //Referencias al valor de el n° de la página actual
            params: {
                page: paginaRef.current
            }
        });

        if (resp.data.data.length > 0) {
            setUsuarios(resp.data.data);
            paginaRef.current++;
        }
    }

    const renderItem = (usuario: Usuario) => {
        return (
            <tr key={usuario.id.toString()}>
                <th>
                    <img src={usuario.avatar.toString()} alt={usuario.first_name.toString()}
                        style={
                            {
                                width: 30,
                                borderRadius: 100
                            }
                        } />
                </th>
                <th>{usuario.first_name.toString() + ' ' + usuario.last_name.toString()}</th>
                <th>{usuario.email.toString()}</th>
            </tr>
        )
    }

    return (
        <>
            <h3>Usuarios</h3>
            <table className='table'>
                <thead>
                    <tr>
                        <th>Avatar</th>
                        <th>Nombre</th>
                        <th>Email</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        usuarios.map(renderItem)
                    }
                </tbody>
            </table>
            <button className='btn btn-primary'>Anterior</button>
            &nbsp;
            <button className='btn btn-primary' onClick={cargarUsuarios}>Siguiente</button>
        </>
    )
}
