import { useState } from 'react';

export const useForm = (formulario: any) => {

    const [state, setState] = useState();

    const onChange = (value: string, campo: string) => {
        setState({
            ...formulario,
            [campo]: value,
        });
    }

    return {
        state,
        onChange
    }

}
